def test_extension_error_with_orig_exc_repr():
    exc = ExtensionError("foo", Exception("bar"))
    if sys.version_info < (3, 7):
        expected = "ExtensionError('foo', Exception('bar',))"
    else:
        expected = "ExtensionError('foo', Exception('bar'))"
    assert repr(exc) == expected

